mapper.select:
SELECT SUM(status)
FROM Food_Like_Dislike
WHERE food_no = #{foodNo}

SELECT COUNT(*) > 0
FROM Food_Like_Dislike
WHERE food_no = #{foodNo}
AND mem_no = #{memNo}

mapper.update:
UPDATE Food_Like_Dislike
SET food_no = #{foodNo},
mem_no = #{memNo},
status = #{status}
WHERE like_dislike_no = #{likeDislikeNo}
