mapper.select:
SELECT COUNT(*) > 0
FROM User
WHERE email = #{email}

SELECT COUNT(*) > 0
FROM User
WHERE nickname = #{nickname}

mapper.insert:
INSERT INTO user (
    email, password, name, height, gender, birthday, phone_number,
    nickname, role
) VALUES (
    #{email}, #{password}, #{name}, #{height}, #{gender}, #{birthday},
    #{phoneNumber}, #{nickname}, #{role}
)

mapper.update:
UPDATE user
SET nickname = #{nickname}, phone_number = #{phoneNumber}, height = #{height}
WHERE email = #{email}

UPDATE user
SET password = #{password}
WHERE email = #{email}
